onMounted.value:
let widgetList = fc.widgetStore().widgetList
let idInput = fc.utils.getWidgetById("a073c85ed808458f8eaaecb47dff7075", widgetList)
let nameInput = fc.utils.getWidgetById("4e6d29f8bd8d423aa22ba8e11dc36580", widgetList)
let table = fc.utils.getWidgetById("b55c7733dfc747b9bbb1d0a104a21e43", widgetList)
let pagination = fc.utils.getWidgetById("53fddf9b369444b49e080a2381e0b734", widgetList)

pagination.options.advanced.currentPage = 1
let currentPage = pagination.options.advanced.currentPage
table.options.advanced.tableValues = []
fc.utils.request({
    url: '/v1/openorg/list',
    method: 'post',
    data: { page: currentPage, page_size: pagination.options.advanced.pageSize }
}).then(response => {
    console.log(fc.widgetStore().widgetList)
    for (let item of response.data.data.list) {
        table.options.advanced.tableValues.push(item)
    }
    pagination.options.advanced.total = response.data.data.total
})

