onClick.value:
if (!props.ruleFormRef) return
props.ruleFormRef.validate((valid, fields) => {
  console.log(valid, "===valid===")
  if (valid) {
    console.log('submit!')
    ElMessage({
      message: '提交成功',
      type: 'success',
      duration: 1000
    })
  } else {
    console.log('error submit!', fields)
    ElMessage({
      message: '有必填项未填写，或填写错误，请检查',
      type: 'error',
      duration: 1000
    })
  }
})

